package demo;

public class nested_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5,b=3;
		if(a>2)
		{
			System.out.println("Grade: A");
		  if(b>5)
		  {
			System.out.println("Grade: B");
		  }
		}
		else
		{
			System.out.println("Grade: D");
		}
	}

}
